import React, { useEffect } from 'react';
import * as d3 from 'd3';

const width = 600 //Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
const height = 500 //Math.max(document.documentElement.clientHeight, window.innerHeight || 0);

const DrawGrapho2 = (props) => {
  useEffect(() => {
   d3.select('.drawGrapho2 > *').remove();
   draw(props)
 },[props])
  return <div className="drawGrapho2" />
}

const draw = (props) => {



  function isNeighborLink(node, link) {
    return link.target.id === node.id || link.source.id === node.id
  }


  function getNodeColor(node, neighbors) {
    if (Array.isArray(neighbors) && neighbors.indexOf(node.id) > -1) {
      return node.level === 1 ? 'blue' : '#e91e63'
    }

    return node.level === 1 ? 'red' : 'gray'
  }


  function getLinkColor(node, link) {
    return isNeighborLink(node, link) ? '#e91e63' : '#E5E5E5'
  }

  function getTextColor(node, neighbors) {
    return Array.isArray(neighbors) && neighbors.indexOf(node.id) > -1 ? '#e91e63' : 'gray'
  }

  var svg = d3.select(".drawGrapho2").append("svg")
  .attr("width", width)
  .attr("height", height);

  var linkElements,
    nodeElements,
    textElements

    // we use svg groups to logically group the elements together
  var linkGroup = svg.append('g').attr('class', 'links')
  var nodeGroup = svg.append('g').attr('class', 'nodes')
  var textGroup = svg.append('g').attr('class', 'texts')

  var selectedId

  d3.json("https://raw.githubusercontent.com/cFabianR/Prubas/master/miserables.json", {crossOrigin: null}).then(function(json) {

    function getNeighbors(node) {
      return json.links.reduce(function (neighbors, link) {
          if (link.target.id === node.id) {
            neighbors.push(link.source.id)
          } else if (link.source.id === node.id) {
            neighbors.push(link.target.id)
          }
          return neighbors
        },
        [node.id]
      )
    }

    var linkForce = d3
      .forceLink()
      .id(function (link) { return link.id })
      .strength(function (link) {return link.value/100})

    var simulation = d3
      .forceSimulation()
      .force('link', linkForce)
      .force('charge', d3.forceManyBody().strength(-20))
      .force('center', d3.forceCenter(width / 2, height / 2))

      var dragDrop = d3.drag().on('start', function (node) {
        node.fx = node.x
        node.fy = node.y
      }).on('drag', function (node) {
        simulation.alphaTarget(0.7).restart()
        node.fx = d3.event.x
        node.fy = d3.event.y
      }).on('end', function (node) {
        if (!d3.event.active) {
          simulation.alphaTarget(0)
        }
        node.fx = null
        node.fy = null
      })

    // select node is called on every click
    // we either update the data according to the selection
    // or reset the data if the same node is clicked twice
    function selectNode(selectedNode) {
      if (selectedId === selectedNode.id) {
        selectedId = undefined
        resetData()
        updateSimulation()
      } else {
        selectedId = selectedNode.id
        updateData(selectedNode)
        updateSimulation()
      }

      var neighbors = getNeighbors(selectedNode)

      // we modify the styles to highlight selected nodes
      nodeElements.attr('fill', function (node) { return getNodeColor(node, neighbors) })
      textElements.attr('fill', function (node) { return getTextColor(node, neighbors) })
      linkElements.attr('stroke', function (link) { return getLinkColor(selectedNode, link) })
    }

    var linkElements = svg.append("g")
      .attr("class", "links")
      .selectAll("line")
      .data(json.links)
      .enter().append("line")
        .attr("stroke-width", 1)
    	  .attr("stroke", "gray")

    var nodeElements = svg.append("g")
      .attr("class", "nodes")
      .selectAll("circle")
      .data(json.nodes)
      .enter().append("circle")
        .attr("r", 10)
        .attr("fill", "#3f51b5")
        .call(dragDrop)
        .on('click', selectNode)

    var textElements = svg.append("g")
      .attr("class", "texts")
      .selectAll("text")
      .data(json.nodes)
      .enter().append("text")
        .text(function (node) { return  node.id})
  	      .attr("font-size", 15)
  	      .attr("dx", 15)
          .attr("dy", 4)


    simulation.nodes(json.nodes).on('tick', () => {
      nodeElements
        .attr('cx', function (node) { return node.x })
        .attr('cy', function (node) { return node.y })
      textElements
        .attr('x', function (node) { return node.x })
        .attr('y', function (node) { return node.y })
      linkElements
        .attr('x1', function (link) { return link.source.x })
        .attr('y1', function (link) { return link.source.y })
        .attr('x2', function (link) { return link.target.x })
        .attr('y2', function (link) { return link.target.y })
    })

    simulation.force("link").links(json.links)

  console.log(json);
  });



}
export default DrawGrapho2
