import React, { useEffect } from 'react';
import * as d3 from 'd3';
import { layout, select, scaleLinear, extent } from "d3";

const styleDefs = require("./bar.scss");

const width = 600 //Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
const height = 500 //Math.max(document.documentElement.clientHeight, window.innerHeight || 0);

const DrawGrapho2 = (props) => {
  useEffect(() => {
   d3.select('.drawGrapho2 > *').remove();
   draw(props)
 },[props])
  return <div className="drawGrapho2" />
}


const draw = (props) => {

  var svg = d3.select(".drawGrapho2").append("svg")
      .attr("width", width)
      .attr("height", height);

      var simulation = d3.forceSimulation()
          .force("link", d3.forceLink().id(function(d) { return d.id; }))
          .force("charge", d3.forceManyBody())
          .force("center", d3.forceCenter());

      d3.json("miserables.json", function(error, graph) {
        if (error) throw error;

        simulation
            .nodes(graph.nodes)
            .on("tick", ticked);

        simulation.force("link")
            .links(graph.links);

        function ticked() {
          context.clearRect(0, 0, width, height);
          context.save();
          context.translate(width / 2, height / 2 + 40);

          context.beginPath();
          graph.links.forEach(drawLink);
          context.strokeStyle = "#aaa";
          context.stroke();

          context.beginPath();
          graph.nodes.forEach(drawNode);
          context.fill();
          context.strokeStyle = "#fff";
          context.stroke();

          context.restore();
        }
      });

      function drawLink(d) {
        context.moveTo(d.source.x, d.source.y);
        context.lineTo(d.target.x, d.target.y);
      }

      function drawNode(d) {
        context.moveTo(d.x + 3, d.y);
        context.arc(d.x, d.y, 3, 0, 2 * Math.PI);
      }
}
export default DrawGrapho2
