import React, { useEffect } from 'react';
import * as d3 from 'd3';
import { layout, select, scaleLinear, extent } from "d3";

const styleDefs = require("./bar.scss");

const width = 600 //Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
const height = 500 //Math.max(document.documentElement.clientHeight, window.innerHeight || 0);

const DrawGrapho2 = (props) => {
  useEffect(() => {
   d3.select('.drawGrapho2 > *').remove();
   draw(props)
 },[props])
  return <div className="drawGrapho2" />
}


const draw = (props) => {


  var simulation = d3
    .forceSimulation()
    .force('charge', d3.forceManyBody().strength(-120))
    .force('center', d3.forceCenter(width / 2, height / 2))

  function getNodeColor(node) {
    return node.group === 1 ? 'red' : 'gray'
  }

  var svg = d3.select(".drawGrapho2").append("svg")
  .attr("width", width)
  .attr("height", height);

  var graph = d3.json("https://raw.githubusercontent.com/cFabianR/Prubas/master/miserables.json", function(error, graph) {
    if (error) throw error;
    var nodeElements = svg.append("g")
      .attr("class", "nodes")
      .selectAll("circle")
      .data(graph.nodes)
      .enter().append("circle")
        .attr("r", 10)
        .attr("fill", getNodeColor)
    console.log(graph.nodes)
  });
  console.log(graph.nodes)
}
export default DrawGrapho2
