import React, { useEffect } from 'react';
import * as d3 from 'd3';
import { layout, select, scaleLinear, extent } from "d3";

const styleDefs = require("./bar.scss");

const width = 600 //Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
const height = 500 //Math.max(document.documentElement.clientHeight, window.innerHeight || 0);

const DrawGrapho = (props) => {
  useEffect(() => {
   d3.select('.drawGrapho > *').remove();
   draw(props)
 },[props])
  return <div className="drawGrapho" />
}

const draw = (props) => {

  var svg = d3.select(".drawGrapho").append("svg")
      .attr("width", width)
      .attr("height", height);

  const simulation = d3.forceSimulation()
      .force('charge', d3.forceManyBody().strength(-20))
      .force('center', d3.forceCenter(width / 2, height / 2))


  d3.json("graph.json", function(json) {
    console.log(json)
  });

  }

export default DrawGrapho
