import React, { useEffect } from 'react';
import * as d3 from 'd3';

const width = 600 //Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
const height = 500 //Math.max(document.documentElement.clientHeight, window.innerHeight || 0);

const DrawGrapho2 = (props) => {
  useEffect(() => {
   d3.select('.drawGrapho2 > *').remove();
   draw(props)
 },[props])
  return <div className="drawGrapho2" />
}

const draw = (props) => {

  var svg = d3.select(".drawGrapho2").append("svg")
  .attr("width", width)
  .attr("height", height);

  d3.json("https://raw.githubusercontent.com/cFabianR/Prubas/master/src/karate_club_graph.json", {
    headers:{
      "Content-type": "application/json; charset=UTF-8",
      "Accept": "application/json",
      "Access-Control-Allow-Methods": "GET, POST, DELETE",
      "Access-Control-Allow-Headers": "Authorization"
    },
  })
  .then(json => {

    console.log(json.nodes)


  });

}
export default DrawGrapho2
