import React, { useEffect } from 'react';
import * as d3 from 'd3';

const width = 600 //Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
const height = 500 //Math.max(document.documentElement.clientHeight, window.innerHeight || 0);

const DrawGrapho2 = (props) => {
  useEffect(() => {
   d3.select('.drawGrapho2 > *').remove();
   draw(props)
 },[props])
  return <div className="drawGrapho2" />
}


const draw = (props) => {

  function getNodeColor(node) {
    return node.group === 1 ? 'red' : 'gray'
  }

  var svg = d3.select(".drawGrapho2").append("svg")
  .attr("width", width)
  .attr("height", height);

  d3.json('https://github.com/cFabianR/Prubas/blob/master/miserables.json', {
    headers: {
      "Content-type": "application/json; charset=UTF-8",
      "Accept": "application/json"
    },
  })
  .then(json => {
   svg.append("text")
    .text(JSON.stringify(json))
    .attr("y", 200)
    .attr("x", 120)
    .attr("font-size", 16)
    .attr("font-family", "monospace")

  });

}
export default DrawGrapho2
