import React, { useEffect } from 'react';
import * as d3 from 'd3';
import { layout, select, scaleLinear, extent } from "d3";

const styleDefs = require("./bar.scss");

const width = 600 //Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
const height = 500 //Math.max(document.documentElement.clientHeight, window.innerHeight || 0);

const DrawGrapho2 = (props) => {
  useEffect(() => {
   d3.select('.drawGrapho2 > *').remove();
   draw(props)
 },[props])
  return <div className="drawGrapho2" />
}


const draw = (props) => {

  var svg = d3.select(".drawGrapho2").append("svg")
      .attr("width", width)
      .attr("height", height);


      var simulation = d3.forceSimulation()
          .force("link", d3.forceLink().id(function(d) { return d.id; }))
          .force("charge", d3.forceManyBody())
          .force("center", d3.forceCenter());

      d3.json("miserables.json", function(error, graph) {
        if (error) throw error;

        console.log(graph)
      });


}
export default DrawGrapho2
